using Terraria.Localization;
using Terraria.ModLoader;

namespace CalamityRuTranslate.CalamityMod.Items
{
	internal class CalamityItemTooltip
	{
		public static void Setup()
		{
			ItemTooltip();
		}
		private static void ItemTooltip()
		{
			Mod calamity = ModLoader.GetMod("CalamityMod");
// ==================================================================================================================
					// Items 
// ==================================================================================================================
			calamity.GetItem("BrimstoneKey").Tooltip.AddTranslation(GameCulture.Russian, "Отпирает все пепельные сундуки");
			calamity.GetItem("BrokenWaterFilter").Tooltip.AddTranslation(GameCulture.Russian,"Пометьте как избранное, чтобы отключить естественное появление Кислотного дождя");
			calamity.GetItem("NormalityRelocator").Tooltip.AddTranslation(GameCulture.Russian,"Одна нога здесь, другая там\nТУТ ModifyTooltips\nТелепортация отключена пока активен дебафф «Состояние хаоса»\nУвеличивает скорость передвижения и падения на 10%\nРаботает, пока находится в инвентаре");
			calamity.GetItem("RelicOfConvergence").Tooltip.AddTranslation(GameCulture.Russian, "Создаёт осквернённый кристалл, набирающий силы\nПока кристалл активен, вы замедлены\nВ конце своего существования кристалл исцеляет игрока");
			calamity.GetItem("Rock").Tooltip.AddTranslation(GameCulture.Russian,"Первый объект, созданный Ксероком");
			calamity.GetItem("SuperDummy").Tooltip.AddTranslation(GameCulture.Russian,"Создаёт сверхманекена\nРегенерирует 1 миллион здоровья в секунду\nНе погибает при получении урона от дебаффов\nНажмите ПКМ, чтобы убить всех сверхманекенов");
// ==================================================================================================================
					// Accessories
// ==================================================================================================================
			calamity.GetItem("Abaddon").Tooltip.AddTranslation(GameCulture.Russian,"Снижает урон от дебаффа «Серные огни»");
			calamity.GetItem("AbyssalAmulet").Tooltip.AddTranslation(GameCulture.Russian,"Все атаки, включая миньонов накладывают дебафф «Давление глубин»\nНаходясь в бездне ваш максимальный запас здоровья увеличен на 10%");
			calamity.GetItem("AbyssalDivingGear").Tooltip.AddTranslation(GameCulture.Russian,"Снижает урон от давления бездны при нехватке воздуха\nУстраняет дебафф «Кровотечение» в бездне\nДарует способность плавать и значительно расширяет подводное дыхание\nОбеспечивает светом под водой и дополнительной мобильностью на льду\nОбеспечивает умеренным количеством света в бездне\nЗначительно снижает потерю дыхания в бездне");
			calamity.GetItem("AbyssalDivingSuit").Tooltip.AddTranslation(GameCulture.Russian,"Превращает носителя в бронированного водолаза\nУвеличивает скорость передвижения под водой и замедляет на суше\nБронированные пластины костюма снижают получаемый урон на 15%\nПластины получают урон только в том случае, если полученный урон превышает 50 единиц\nПосле того как костюм получил слишком много урона, его бронированным пластинам понадобится 3 минуты на восстановление\nСнижает урон от давления бездны при нехватке воздуха\nУстраняет дебафф «Кровотечение» на всех слоях бездны, кроме самого глубокого\nДарует способность плавать и значительно расширяет подводное дыхание\nОбеспечивает светом под водой и дополнительной мобильностью на льду\nОбеспечивает умеренным количеством света в бездне\nЗначительно снижает потерю дыхания в бездне\nСнижает способность существ обнаружить вас в бездне\nУменьшает снижение защиты, вызываемое бездной\nПозволяет вам быстрее падать в жидкостях");
			calamity.GetItem("AbyssalMirror").Tooltip.AddTranslation(GameCulture.Russian,"Свет не достигает глубин океана\nЗначительно снижает вражескую агрессию, даже в бездне\nСкрытность восстанавливается на 30% быстрее, пока вы стоите на месте, и на 20% быстрее, когда движетесь\nДарует небольшой шанс уклониться от атаки, высвобождая облако люмениловой жидкости, которая наносит урон и оглушает ближайших врагов\nУклонение от атаки даёт много скрытности\nЭто уклонение имеет 20 секунд перезарядки перед повторным срабатыванием");
			calamity.GetItem("AeroStone").Tooltip.AddTranslation(GameCulture.Russian,"Одна из древних реликвий\nТУТ ModifyTooltips");
			calamity.GetItem("Affliction").Tooltip.AddTranslation(GameCulture.Russian,"Даёт вам и всем другим игрокам в вашей команде +1 регенерации здоровья\nУвеличивает максимальный запас здоровья на 10%, сопротивление урону на 7%, защиту на 20, и урон на 10%");
			calamity.GetItem("AlchemicalFlask").Tooltip.AddTranslation(GameCulture.Russian,"Все атаки, включая миньонов накладывают дебафф «Чума»\nСнижает урон от дебаффа «Чума»\nУстраняет эффект слепоты от дебаффа «Чума»\nВаши снаряды создают чумных искателей при попадании во врага");
			calamity.GetItem("AlluringBait").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает силу рыбалки в течение дня на 30\nУвеличивает силу рыбалки в ночное время на 45\nУвеличивает силу рыбалки во время солнечного затмения на 60\nЗначительно увеличивает шанс ловли рыбы необходимую для зелий");
			calamity.GetItem("AmalgamatedBrain").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает урон на 10%\nПри получении урона с неба обрушиваются тени\nНакладывает на ближайших врагов дебафф «Запутанность» при контакте с вами");
			calamity.GetItem("AmbrosialAmpoule").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость добычи на 25%\nВы излучаете свет\nУвеличивает сопротивление урону на 5% и регенерацию здоровья\nНевосприимчивость к дебаффам «Отравление», «Заморозка», «Охлаждение», «Ледяной ожог» и «Яд»\nБафф «Мёд» без штрафа скорости\nБольшинство пчёл/шершней и их снарядов наносят вам только 75% урона");
			calamity.GetItem("AmidiasPendant").Tooltip.AddTranslation(GameCulture.Russian,"Периодически обрушивает осколки призм, которые могут на короткий миг оглушить врагов");
			calamity.GetItem("AmidiasSpark").Tooltip.AddTranslation(GameCulture.Russian,"Вы испустите заряд искр при получении урона\nИскры наносят дополнительный урон в Хардмоде");
			calamity.GetItem("AncientFossil").Tooltip.AddTranslation(GameCulture.Russian,"Находясь под землёй, увеличивает скорость добычи на 15%");
			calamity.GetItem("AnechoicPlating").Tooltip.AddTranslation(GameCulture.Russian,"Снижает способность существ обнаружить вас в бездне\nУменьшает снижение защиты, вызываемое бездной");
			calamity.GetItem("AngelTreads").Tooltip.AddTranslation(GameCulture.Russian,"Экстремальная скорость!\nУвеличивает ускорение при беге на 36%\nУвеличивает время полёта\nОтличная мобильность на льду\nХодьба по воде и лаве\nВременная невосприимчивость к лаве");
			calamity.GetItem("AquaticEmblem").Tooltip.AddTranslation(GameCulture.Russian,"Большинство существ океана становятся дружелюбными; обеспечивает подводным дыханием\nБудучи под водой медленно увеличивает вашу защиту со временем, но также замедляет скорость передвижения\nУвеличение защиты и снижение скорости передвижения медленно исчезают за пределами воды\nМаксимальное усиление защиты — 30, максимальное снижение скорости передвижения — 5%\nОбеспечивает небольшим количеством света в бездне\nУмеренно снижает потерю дыхания в бездне");
			calamity.GetItem("ArcanumoftheVoid").Tooltip.AddTranslation(GameCulture.Russian,"У вас есть 5% шанс отразить снаряды, которые попадут в вас\nЕсли этот эффект срабатывает, вы исцеляетесь на значение от урона снаряда");
			calamity.GetItem("ArchaicPowder").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость добычи на 20%, сопротивление урону на 7% и защиту на 3 находясь под землёй или в аду");
			calamity.GetItem("AsgardianAegis").Tooltip.AddTranslation(GameCulture.Russian,"Дарует невосприимчивость к огненным блокам и отбрасыванию\nНевосприимчивость к большинству дебаффов\nУвеличивает максимальный запас здоровья на 40\nДарует высший рывок святого пламени\nМожно использовать для тарана врагов\nТУТ ModifyTooltips\nАктивация этого баффа уменьшит вашу скорость передвижения и увеличит вражескую агрессию\n10% сопротивление урону в жидкостях");
			calamity.GetItem("AsgardsValor").Tooltip.AddTranslation(GameCulture.Russian,"Дарует невосприимчивость к огненным блокам и отбрасыванию\nНевосприимчивость к большинству дебаффов и снижает урон от дебаффа «Серные огни»\n10% сопротивление урону в жидкостях\nУвеличивает максимальный запас здоровья на 20\nДарует святой рывок\nМожно использовать для тарана врагов");
			calamity.GetItem("AstralArcanum").Tooltip.AddTranslation(GameCulture.Russian,"При получении урона сбрасывает с неба астральные звёзды\nОбеспечивает невосприимчивостью к дебаффу «Астральная инфекция»\nУ вас есть 5% шанс отразить снаряды, которые попадут в вас\nЕсли этот эффект срабатывает, вы исцеляетесь на значение от урона снаряда\nУскоряет регенерацию здоровья, даже под воздействием дебаффа, наносящего урон\nВо время действия дебаффа, наносящего урон, вы получаете 20 единиц защиты\nТУТ ModifyTooltips");
			calamity.GetItem("AstralBulwark").Tooltip.AddTranslation(GameCulture.Russian,"При получении урона сбрасывает с неба астральные звёзды\nОбеспечивает невосприимчивостью к дебаффу «Астральная инфекция»");
			calamity.GetItem("BadgeofBravery").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость атаки ближнего боя на 15%\nПока на вас надет комплект таррагона увеличивает урон ближнего боя, шанс критического удара и пробивание брони");
			calamity.GetItem("BlazingCore").Tooltip.AddTranslation(GameCulture.Russian,"Обжигающее ядро осквернённой богини\nУвеличивает сопротивление урону на 10%\nПри получении урона, создаёт миниатюрное солнце, которое задерживается на месте и наносит урон ближайшим врагам\nСолнце медленно втягивает врагов\nТолько одно солнце может быть активно за раз\nОбеспечивает умеренным количеством света в бездне");
			calamity.GetItem("BloodflareCore").Tooltip.AddTranslation(GameCulture.Russian,"Вы теряете до 50% от максимальной защиты после получения урона\nПотерянная защита восстанавливается с течением времени\nИсцеляет 1 единицу здоровья за каждую восстановленную единицу защиты");
			calamity.GetItem("BloodPact").Tooltip.AddTranslation(GameCulture.Russian,"Удваивает максимальный запас здоровья\nВы получаете критический удар с 25% шансом\nПосле критического удара вы получаете различные баффы на десять секунд\nЛюбые лечебные зелья, потребляемые в этот период, восстанавливают на 50% больше здоровья");
			calamity.GetItem("BloodstainedGlove").Tooltip.AddTranslation(GameCulture.Russian,"Скрытные удары пробивают 10 брони и исцеляют на 1 единицу здоровья");
			calamity.GetItem("BloodyWormScarf").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает сопротивление урону и характеристики ближнего боя на 10%");
			calamity.GetItem("BloodyWormTooth").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает сопротивление урону и характеристики ближнего боя на 5%\nКогда здоровье ниже 50% увеличивает сопротивление урону и характеристики ближнего боя на 10%");
			calamity.GetItem("BloomStone").Tooltip.AddTranslation(GameCulture.Russian,"Одна из древних реликвий\nПриближающиеся враги получают урон; увеличивает весь урон на 3%\nВы выращиваете цветы на траве под вами. Шанс вырастить случайное растение-краситель на бестравяной земле");
			calamity.GetItem("BlunderBooster").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает разбойный урон на 12% и скорость снарядов разбойника на 15%\nПризывает ауру красной молнии, которая окружает игрока и электрифицирует ближайших врагов\nТУТ ModifyTooltips\nЭтот эффект имеет 3 секунды перезарядки перед повторным использованием");
			calamity.GetItem("BurdenBreaker").Tooltip.AddTranslation(GameCulture.Russian,"Тяжёлые времена\nУбирает кадры неуязвимости\nЕсли вы хотите сумасшедшее испытание, экипируйте это\nСоприкосновение с лавой мгновенно убивает вас");
			calamity.GetItem("CalamityRing").Tooltip.AddTranslation(GameCulture.Russian,"Проклято? Уменьшает сопротивление урону на 10%\nУвеличивает весь урон на 15%\nПока активна неуязвимость с неба обрушиваются серные огни");
			calamity.GetItem("CelestialJewel").Tooltip.AddTranslation(GameCulture.Russian,"Ускоряет регенерацию здоровья, даже под воздействием дебаффа, наносящего урон\nВо время действия дебаффа, наносящего урон, вы получаете 20 единиц защиты\nТУТ ModifyTooltips");
			calamity.GetItem("ChaosAmulet").Tooltip.AddTranslation(GameCulture.Russian,"Эффект зелья шахтёра; увеличивает регенерацию здоровья");
			calamity.GetItem("ChaosStone").Tooltip.AddTranslation(GameCulture.Russian,"Одна из древних реликвий\nУвеличивает максимальный запас маны на 50, весь урон на 3% и снижает расход маны на 5%");
			calamity.GetItem("CoinofDeceit").Tooltip.AddTranslation(GameCulture.Russian,"Скрытные удары расходуют только 75% вашей максимальной скрытности\nУвеличивает разбойный шанс критического удара на 6%");
			calamity.GetItem("CoreOfTheBloodGod").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает урон и сопротивление урону на 8%\nУвеличивает максимальный запас здоровья на 10%\nЛечебные зелья на 15% эффективнее\nВдвое снижает контактный урон от врагов\nПосле получения контактного урона этот эффект перезаряжается 20 секунд");
			calamity.GetItem("CorrosiveSpine").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость передвижения на 10%\nРазбойное оружие накладывает дебафф «Яд» и создаёт облака при ударе по врагу\nПри получении урона выпускает повсюду массу облаков");
			calamity.GetItem("CorruptFlask").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает сопротивление урону на 7% и защиту на 3, находясь в искажении");
			calamity.GetItem("CounterScarf").Tooltip.AddTranslation(GameCulture.Russian,"Истинные атаки ближнего боя наносят на 15% больше урона\nДарует способность совершать рывок; совершая рывок на атаку врага, заставит вас уклониться от неё\nПосле удачного уклонения дарует бафф, увеличивающий весь урон, скорость атаки и шанс критического удара ближнего боя на короткое время\nПосле успешного уклонения вы должны подождать 15 секунд, прежде чем сможете уклониться снова\nПерезарядка продлится вдвое больше, если на вас наложен дебафф «Состояние хаоса»\nВо время перезарядки, «Состояние хаоса» продлится вдвое дольше\nПредмет режима Месть");
			calamity.GetItem("CrawCarapace").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает сопротивление урону на 5%\nВраги получают урон, когда касаются вас");
			calamity.GetItem("CrimsonFlask").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает сопротивление урону на 7% и защиту на 3, находясь в багрянце");
			calamity.GetItem("CrownJewel").Tooltip.AddTranslation(GameCulture.Russian,"Ускоряет регенерацию здоровья, даже под воздействием дебаффа, наносящего урон\nВо время действия дебаффа, наносящего урон, вы получаете 10 единиц защиты\nПредмет режима Месть");
			calamity.GetItem("CryoStone").Tooltip.AddTranslation(GameCulture.Russian,"Одна из древних реликвий\nУвеличивает сопротивление урону на 5% и весь урон на 3%");
			calamity.GetItem("DaedalusEmblem").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает стрелковый урон на 10% и шанс критического удара на 5%; 20% шанс не использовать боеприпасы\nУвеличивает защиту на 5, регенерацию здоровья на 2 и скорость добычи на 15%");
			calamity.GetItem("DarkGodsSheath").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальную скрытность на 20\nВосстановление подвижной скрытности ускоряется, пока вы не атакуете\nСкрытные удары имеют 100% шанс критического удара\nСкрытные удары расходуют только 50% вашей максимальной скрытности\nУвеличивает разбойный урон и шанс критического удара на 6%");
			calamity.GetItem("DarkSunRing").Tooltip.AddTranslation(GameCulture.Russian,"Содержит силу тёмного солнца\nУвеличивает урон и скорость атаки ближнего боя на 12%\nУвеличивает отбрасывание миньонов\nУвеличивает регенерацию здоровья на 1, скорость добычи на 15% и максимальное число миньонов на 2\nВ течение дня увеличивает у игрока регенерацию здоровья на 3\nНочью игрок получает 30 единиц защиты\nВо время солнечного затмения дарует оба этих бонуса");
			calamity.GetItem("DeepDiver").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает урон, защиту и скорость передвижения на 15%, находясь под водой\nПод водой вы получаете способность совершать рывок на большие расстояния");
			calamity.GetItem("DeificAmulet").Tooltip.AddTranslation(GameCulture.Russian,"При получении урона делает вас очень быстрым на короткое время\nЗаставляет падать звёзды и увеличивает время неуязвимости после получения урона\nУвеличивает пробивание брони на 10 и уменьшает перезарядку лечебных зелий\nОбеспечивает светом под водой и небольшим количеством света в бездне");
			calamity.GetItem("DepthCharm").Tooltip.AddTranslation(GameCulture.Russian,"Снижает урон от давления бездны при нехватке воздуха\nУстраняет дебафф «Кровотечение» на верхних слоях бездны");
			calamity.GetItem("DimensionalSoulArtifact").Tooltip.AddTranslation(GameCulture.Russian,"Сила\nУвеличивает весь урон на 25% но какой ценой?\nУвеличивает весь получаемый урон на 15%");
			calamity.GetItem("DraedonsHeart").Tooltip.AddTranslation(GameCulture.Russian,"Вы наносите на 10% больше урона, пока на вас наложен бафф «Абсолютная ярость»\nУвеличивает шанс получить бафф «Абсолютная ярость»\nУвеличивает урон, максимальную скорость передвижения и ускорение на 5%\nРежим ярости наносит больше урона\nВы получаете ярость постоянно\nПреобразовывает некоторые дебаффы в баффы и увеличивает их длительность\nЗатрагиваемые дебаффы: «Тьма», «Чёрная пелена», «Запутанность», «Медленный», «Слабый», «Сломанная броня», «Треск брони», «Боевое рассечение», «Охлаждение», «Ихор», «Затемнение»\nПолучив удар, вы теряете только половину своего максимального адреналина, а не весь\nПока вы стоите на месте, вы быстро регенерируете здоровье и повышаете свою защиту на 25");
			calamity.GetItem("DragonScales").Tooltip.AddTranslation(GameCulture.Russian,"Только живой дракон хранит у себя истинное сокровище\nРазбойные снаряды создают медленные огненные шары во время полёта\nСкрытные удары создают инфернадо при попадании\nПосле убийства Ярона увеличивает скорость передвижения и ускорение на 10%");
			calamity.GetItem("DukeScales").Tooltip.AddTranslation(GameCulture.Russian,"Во время действия дебаффа, наносящего урон, увеличивает урон на 10% и шанс критического удара на 5%\nЗа каждые 25% потерянного здоровья увеличивает урон на 6% и шанс критического удара на 3%\nМаксимальное значение будет составлять 18% увеличенного урона и 9% шанса критического удара при менее чем 25% здоровья\nОбеспечивает невосприимчивостью к дебаффам «Отравление», «Яд» и «Сернистое отравление»");
			calamity.GetItem("DynamoStemCells").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость передвижения на 15%\nСтрелковое оружие иногда выпускает миниатюрный рой");
			calamity.GetItem("EclipseMirror").Tooltip.AddTranslation(GameCulture.Russian,"В его отражении нет ничего, кроме темноты\nУвеличивает максимальную скрытность на 20\nУвеличивает разбойный урон и шанс критического удара на 6%\nЗначительно снижает вражескую агрессию, даже в бездне\nСкорость восстановления скрытности на 20% быстрее, пока вы стоите на месте\nВосстановление подвижной скрытности ускоряется, пока вы не атакуете\nСкрытные удары имеют 100% шанс критического удара\nСкрытные удары расходуют только 50% вашей максимальной скрытности\nДаёт небольшой шанс клониться от атак во вспышке света тёмного солнца, который наносит огромный урон в обширной области\nУклонение от атаки даёт полную скрытность\nЭто уклонение имеет 20 секунд перезарядки перед повторным срабатыванием");
			calamity.GetItem("EldritchSoulArtifact").Tooltip.AddTranslation(GameCulture.Russian,"Знания\nУвеличивает скорость атаки ближнего боя на 10%, скорость стрелковых снарядов на 25%, и разбойный урон на 15%\nУвеличивает максимальное число миньонов на 2 и снижает расход маны на 15%");
			calamity.GetItem("ElectriciansGlove").Tooltip.AddTranslation(GameCulture.Russian,"Скрытные удары вызывают искры при попадании во врага\nСкрытные удары пробивают 30 брони, наносят на 10% больше урона и исцеляют на 1 единицу здоровья");
			calamity.GetItem("ElementalGauntlet").Tooltip.AddTranslation(GameCulture.Russian,"Атаки и снаряды ближнего боя накладывают различные дебаффы\nУвеличивает скорость атаки и урон ближнего боя на 15%, и шанс критического удара на 5%\nУвеличивает истинный урон ближнего боя на 20%\nУвеличивает длительность неуязвимости при получении урона\nВременная невосприимчивость к лаве\nУвеличивает отбрасывание ближнего боя");
			calamity.GetItem("ElementalQuiver").Tooltip.AddTranslation(GameCulture.Russian,"Стрелковые снаряды могут разделиться\nУвеличивает стрелковый урон на 15% и шанс критического удара на 5%; 20% шанс не использовать боеприпасы\nУвеличивает защиту на 5, регенерацию здоровья на 2 и скорость добычи на 15%\nЗначительно увеличивает скорость стрел; 20% шанс не использовать стрелы");
			calamity.GetItem("ElysianAegis").Tooltip.AddTranslation(GameCulture.Russian,"Благословлён осквернённым пламенем\nДарует невосприимчивость к огненным блокам, отбрасыванию и дебаффу «Святые огни»\nУвеличивает максимальный запас здоровья на 40 и регенерацию здоровья\nДарует высший святой огненный рывок\nМожно использовать для тарана врагов\nТУТ ModifyTooltips\nАктивация этого баффа уменьшит вашу скорость передвижения и увеличит вражескую агрессию\n10% сопротивление урону в жидкостях");
			calamity.GetItem("EnchantedPearl").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает навык рыбалки\nУвеличивает шанс поймать ящики");
			calamity.GetItem("EtherealExtorter").Tooltip.AddTranslation(GameCulture.Russian,"Наполняет душами ваше оружие и тело, создавая различные усиления, которые меняются в зависимости от окружающей среды\nРазбойные снаряды редко разрываются самонаводящимися душами\nУвеличивает разбойный урон на 10%, но уменьшает регенерацию здоровья");
			calamity.GetItem("EtherealTalisman").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает магический урон на 15%, шанс критического удара на 5% и снижает расход маны на 10%\nУвеличивает максимальный запас маны на 150; показывает местонахождение сокровищ, если видимость аксессуара включена\nУвеличивает дальность подбора звёзд маны и восстанавливает ману при получении урона\nВы автоматически используете зелья маны при необходимости, если видимость аксессуара включена");
			calamity.GetItem("EvasionScarf").Tooltip.AddTranslation(GameCulture.Russian,"Истинные удары ближнего боя наносят на 25% больше урона\nДарует способность совершать рывок; совершая рывок на атаку врага, заставит вас уклониться от неё\nПосле удачного уклонения дарует бафф, увеличивающий весь урон, скорость атаки и шанс критического удара ближнего боя на короткое время\nПосле успешного уклонения вы должны подождать 13 секунд, прежде чем сможете уклониться снова\nПерезарядка продлится на 50% дольше, если на вас наложен дебафф «Состояние хаоса»\nВо время перезарядки, «Состояние хаоса» продлится на 50% дольше");
			calamity.GetItem("EyeoftheStorm").Tooltip.AddTranslation(GameCulture.Russian,"Призывает облачного элементаля сражаться за вас");
			calamity.GetItem("FabledTortoiseShell").Tooltip.AddTranslation(GameCulture.Russian,"Снижает скорость передвижения на 50%\nВраги получают урон, когда бьют вас\nПри получении урона делает вас очень быстрым на короткое время");
			calamity.GetItem("FeatherCrown").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость снарядов разбойника на 15%\nСкрытные удары заставляют перья падать с неба при попадании во врага");
			calamity.GetItem("FilthyGlove").Tooltip.AddTranslation(GameCulture.Russian,"Скрытные удары пробивают 10 брони и наносят на 10% больше урона");
			calamity.GetItem("FleshTotem").Tooltip.AddTranslation(GameCulture.Russian,"Вдвое снижает контактный урон от врагов\nПри получении контактного урона этот эффект перезаряжается 20 секунд");
			calamity.GetItem("FrigidBulwark").Tooltip.AddTranslation(GameCulture.Russian,"Поглощает 25% урона, нанесённого игрокам в вашей команде\nАктивно, только если здоровье выше 25%\nДарует невосприимчивость к отбрасыванию\nОблачает владельца в панцирь, который снижает урон, когда здоровье ниже 50%\nПанцирь становится сильнее и ещё больше снижает урон, когда здоровье ниже 15%");
			calamity.GetItem("FrostBarrier").Tooltip.AddTranslation(GameCulture.Russian,"Вы заморозите ближайших врагов рядом с вами, когда вас ранят\nВы невосприимчивы к дебаффу «Охлаждение»");
			calamity.GetItem("FrostFlare").Tooltip.AddTranslation(GameCulture.Russian,"Все атаки и снаряды ближнего боя накладывают дебафф «Ледяной ожог»\nНевосприимчивость к дебаффам «Ледяной ожог», «Охлаждение» и «Заморозка»\nУстойчивость к холодным атакам и увеличивает регенерацию здоровья на 1\nКогда здоровье выше 75% увеличивает урон на 10%\nКогда здоровье ниже 25% увеличивает защиту на 10, максимальную скорость передвижения и ускорение на 15%\nПредмет режима Месть");
			calamity.GetItem("FungalCarapace").Tooltip.AddTranslation(GameCulture.Russian,"Вы испускаете грибные споры при получении урона");
			calamity.GetItem("FungalClump").Tooltip.AddTranslation(GameCulture.Russian,"Призывает грибковую глыбу сражаться за вас\nГлыба цепляется за врагов и крадёт их жизнь для вас");
			calamity.GetItem("FungalSymbiote").Tooltip.AddTranslation(GameCulture.Russian, "Различные виды оружия ближнего боя испускают грибы на расстояние истинного ближнего удара\nИстинные удары ближнего боя наносят на 15% больше урона");
			calamity.GetItem("Gehenna").Tooltip.AddTranslation(GameCulture.Russian,"Время от времени сбрасывает с неба серные огненные шары");
			calamity.GetItem("GiantPearl").Tooltip.AddTranslation(GameCulture.Russian,"Вокруг вас аура света\nВраги внутри ауры замедляются\nНе работает, пока босс жив\nОбеспечивает небольшим количеством света в бездне");
			calamity.GetItem("GiantShell").Tooltip.AddTranslation(GameCulture.Russian,"Снижает скорость передвижения на 15%\nПолучение урона делает вас очень быстрым на короткое время");
			calamity.GetItem("GiantTortoiseShell").Tooltip.AddTranslation(GameCulture.Russian,"Снижает скорость передвижения на 10%\nВраги получают урон, когда бьют вас");
			calamity.GetItem("GladiatorsLocket").Tooltip.AddTranslation(GameCulture.Russian,"Призывает два духовных меча защищать вас");
			calamity.GetItem("GloveOfPrecision").Tooltip.AddTranslation(GameCulture.Russian,"Уменьшает скорость оружия разбойника на 20%, но увеличивает разбойный урон и шанс критического удара на 12%, и скорость снарядов на 25%");
			calamity.GetItem("GloveOfRecklessness").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость оружия разбойника на 20%, но уменьшает урон на 10%\nДобавляет неточность разбойному оружию");
			calamity.GetItem("GodlySoulArtifact").Tooltip.AddTranslation(GameCulture.Russian,"Верность\nПризывает двух Сыновей Ярона сражаться за вас");
			calamity.GetItem("GoldBurdenBreaker").Tooltip.AddTranslation(GameCulture.Russian,"Хорошее время\nДавай быстрее\nПРЕДУПРЕЖДЕНИЕ: Может иметь катастрофические последствия\nУвеличивает горизонтальную скорость передвижения за пределами понимания");
			calamity.GetItem("GrandGelatin").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость передвижения на 10%\nТУТ ModifyTooltips\nУвеличивает максимальный запас здоровья и маны на 20\nСтоя на месте ускоряет регенерацию здоровья и маны");
			calamity.GetItem("GravistarSabaton").Tooltip.AddTranslation(GameCulture.Russian,"Нажмите кнопку ВНИЗ, чтобы увеличить скорость падения на 5 секунд\nИмеет 8 секунд перезарядки\nУдар по земле с повышенной скоростью падения вызывает астральный взрыв");
			calamity.GetItem("HallowedRune").Tooltip.AddTranslation(GameCulture.Russian,"Всякий раз, когда ваши миньоны атакуют врага, вы получаете случайный бафф\nЭти баффы на короткое время увеличивают вашу защиту, урон миньонов или регенерацию здоровья\nЕсли у вас есть атакующее усиление, враги, поражённые миньонами, иногда будут поражены звёздами");
			calamity.GetItem("HarpyRing").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость передвижения на 20%\nУвеличивает ваше максимальное время полёта на 25%");
			calamity.GetItem("HeartofDarkness").Tooltip.AddTranslation(GameCulture.Russian,"Вы наносите на 10% больше урона, пока на вас наложен бафф «Абсолютная ярость»\nУвеличивает шанс получить бафф «Абсолютная ярость»\nРежим ярости наносит больше урона\nВы получаете ярость постоянно\nПредмет режима Месть");
			calamity.GetItem("HeartoftheElements").Tooltip.AddTranslation(GameCulture.Russian,"Сердце мира\nУвеличивает максимальный запас здоровья на 20, регенерацию здоровья на 1 и весь урон на 5%\nУвеличивает скорость передвижения на 10% и скорость прыжка на 20%\nУвеличивает сопротивление урону на 5%\nУвеличивает максимальный запас маны на 50 и снижает расход маны на 5%\nВы выращиваете цветы на траве под вами. Шанс вырастить случайное растение-краситель на бестравяной земле\nПризывает всех элементалей защищать вас\nПереключение видимости этого аксессуара также включает и выключает элементалей\nУвеличение характеристик немного выше, если элементали выключены");
			calamity.GetItem("HideofAstrumDeus").Tooltip.AddTranslation(GameCulture.Russian,"При получении урона сбрасывает с неба огромное количество астральных звёзд и увеличивает истинный урон ближнего боя на 50% в течение некоторого времени\nДлительность увеличения зависит от количества нанесённого урона, чем выше урон, тем дольше увеличение\nОбеспечивает невосприимчивостью к дебаффам «Астральная инфекция», «Проклятие ада», «В огне!» и «Ледяной ожог»\nВраги получают урон, когда бьют вас и накладывает на них дебафф «Астральная инфекция»");
			calamity.GetItem("HoneyDew").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает сопротивление урону на 5%, защиту на 5 и регенерацию здоровья находясь в джунглях\nНевосприимчивость к дебаффам «Отравление» и «Яд»\nБафф «Мёд» без штрафа скорости\nБольшинство пчёл/шершней и их снарядов наносят вам только 75% урона");
			calamity.GetItem("HowlsHeart").Tooltip.AddTranslation(GameCulture.Russian,"Призывает Хаула сражаться за вас, Кальцифер освещает ваш путь, Пугало-Репка следует за вами повсюду");
			calamity.GetItem("InkBomb").Tooltip.AddTranslation(GameCulture.Russian,"Бросает несколько чернильных бомб, при попадании которые разрываются запутанным облаком чернил\nДаёт много скрытности при получении урона\nЭтот эффект имеет 20 секунд перезарядки перед повторным срабатыванием");
			calamity.GetItem("IronBoots").Tooltip.AddTranslation(GameCulture.Russian,"Позволяет вам быстрее падать в жидкостях");
			calamity.GetItem("JellyChargedBattery").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальное число миньонов на 1 и урон миньонов на 7%\nАтаки миньонов создают шары энергии и накладывают дебафф «Электрошок»");
			calamity.GetItem("Laudanum").Tooltip.AddTranslation(GameCulture.Russian,"Преобразовывает некоторые дебаффы в баффы и увеличивает их длительность\nЗатрагиваемые дебаффы: «Тьма», «Чёрная пелена», «Запутанность», «Медленный», «Слабый», «Сломанная броня», «Треск брони», «Боевое рассечение», «Охлаждение», «Ихор», «Затемнение»\nПредмет режима Месть");
			calamity.GetItem("LeadCore").Tooltip.AddTranslation(GameCulture.Russian,"Дарует невосприимчивость к дебаффу «Облучённый»");
			calamity.GetItem("LeviathanAmbergris").Tooltip.AddTranslation(GameCulture.Russian,"Вы оставляете после себя ядовитую морскую воду, пока вы движетесь\nУвеличивает скорость передвижения на 75%, весь урон на 10% и защиту на 20 в жидкостях\nЕсли вас ранили в жидкостях, вы получаете разрушающую ауру на короткое время\nУвеличивает весь урон и сопротивление урону на 5% вне жидкостей");
			calamity.GetItem("LifeJelly").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальный запас здоровья на 20\nПока вы стоите на месте, ускоряет регенерацию здоровья");
			calamity.GetItem("LivingDew").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает сопротивление урону на 5%, защиту на 5 и регенерацию здоровья находясь в джунглях\nНевосприимчивость к дебаффу «Яд»");
			calamity.GetItem("LumenousAmulet").Tooltip.AddTranslation(GameCulture.Russian,"Все атаки, включая миньонов накладывают дебафф «Давление глубин»\nНаходясь в бездне ваш максимальный запас здоровья увеличен на 25%\nОбеспечивает умеренным количеством света в бездне");
			calamity.GetItem("LureofEnthrallment").Tooltip.AddTranslation(GameCulture.Russian,"Призывает водного элементаля сражаться за вас\nЭлементаль парит над вами, стреляя водяными копьями, ледяным туманом и скрипичными ключами в ближайших врагов");
			calamity.GetItem("LuxorsGift").Tooltip.AddTranslation(GameCulture.Russian,"Оружие запускает уникальные снаряды в зависимости от типа наносимого им урона\nНекоторые виды оружия не могут получить этот бонус");
			calamity.GetItem("ManaJelly").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальный запас маны на 20\nПока вы стоите на месте, ускоряет регенерацию маны");
			calamity.GetItem("ManaOverloader").Tooltip.AddTranslation(GameCulture.Russian, "Увеличивает максимальный запас маны на 50 и магический урон на 6%\nРегенерация здоровья снижена на 3, если мана превышает 50% от максимального значения\nДарует спектральное исцеление, сила исцеления зависит от количества вашей маны\nИсцеление работает только в том случае, если вы держите магическое оружие\nЧем больше у вас маны, тем больше вы исцеляетесь");
			calamity.GetItem("MirageMirror").Tooltip.AddTranslation(GameCulture.Russian,"Сгибайте свет вокруг вас\nСнижает вражескую агрессию за пределами бездны\nУвеличивает скорость восстановление скрытности при движении на 10%\nСкрытность восстанавливается на 30% быстрее, пока вы стоите на месте, и на 20% быстрее, когда движетесь");
			calamity.GetItem("MomentumCapacitor").Tooltip.AddTranslation(GameCulture.Russian,"ТУТ ModifyTooltips\nРазбойные снаряды, которые проходят через поле, получают постоянное ускорение и увеличение урона на 15%\nЭти усиления могут быть наложены на снаряд лишь один раз\nМожет быть только одно поле");
			calamity.GetItem("MoonstoneCrown").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость снарядов разбойника на 15%\nСкрытные удары вызывают лунные вспышки при попадании во врага\nРазбойные снаряды очень редко вызывают позади себя лунные знаки");
			calamity.GetItem("MutatedTruffle").Tooltip.AddTranslation(GameCulture.Russian,"Призывает маленького Старого Герцога сражаться за вас\nКогда здоровье ниже 50%, он движется гораздо быстрее");
			calamity.GetItem("Nanotech").Tooltip.AddTranslation(GameCulture.Russian,"Разбойные снаряды создают нанолезвия\nСкрытные удары вызывают лунные вспышки и искры при попадании во врага\nСкрытные удары пробивают 20 брони, наносят на 5% больше урона и исцеляют на 1 единицу здоровья\nУвеличивает разбойный урон на 12% и скорость снарядов разбойника на 15%\nВсякий раз, когда вы наносите критический удар разбойным оружием, ваш разбойный урон увеличивается\nЭтот эффект может складываться до 150 раз\nМаксимальное увеличение разбойного урона составляет 10%\nТУТ ModifyTooltips");
			calamity.GetItem("NebulousCore").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает урон на 12%\nПризывает парящие звёзды туманности защищать вас\nУ вас есть 10% шанс пережить атаку, которая убила бы вас\nЕсли этот эффект срабатывает, вы будете исцелены на 100 единиц здоровья");
			calamity.GetItem("NecklaceofVexation").Tooltip.AddTranslation(GameCulture.Russian,"Месть\nУвеличивает урон на 20%, когда здоровье ниже 50%\nВсе атаки, включая миньонов накладывают дебаффы «Проклятие ада» и «Яд» пока на вас надет комплект разорителя");
			calamity.GetItem("NuclearRod").Tooltip.AddTranslation(GameCulture.Russian,"Миньоны выпускают облучающую ауру при попадании во врага\nУвеличивает максимальное число миньонов на 1");
			calamity.GetItem("Nucleogenesis").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальное число миньонов на 4 и урон миньонов на 15%\nУвеличивает отбрасывание миньонов\nМиньоны накладывают различные дебаффы\nМиньоны создают разрушающие искры при попадании");
			calamity.GetItem("OceanCrest").Tooltip.AddTranslation(GameCulture.Russian,"Большинство существ океана становятся дружелюбными; обеспечивает подводным дыханием");
			calamity.GetItem("OldDie").Tooltip.AddTranslation(GameCulture.Russian,"Ваша удача, что проклятье не влияет на вас. В большинстве случаев\nУвеличивает разброс урона при атаке");
			calamity.GetItem("OrnateShield").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает сопротивление урону и здоровье пока на вас надет комплект дедала");
			calamity.GetItem("PermafrostsConcoction").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальный запас маны на 50 и снижает расход маны на 15%\nУвеличивает регенерацию здоровья по мере его уменьшения\nУвеличивает регенерацию здоровья при воздействии на вас дебаффов «Отравление», «В огне!» или «Серные огни»\nВы переживёте смертельный урон и возродитесь с 30% здоровья. Эта способность перезаряжается 3 минуты\nВы находитесь в ледяном барьере в течение 3 секунд после возрождения");
			calamity.GetItem("PlaguedFuelPack").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает разбойный урон на 5%\nУвеличивает скорость снарядов разбойника на 15%\nТУТ ModifyTooltips\nЭтот эффект имеет 3 секунды перезарядки перед повторным использованием");
            calamity.GetItem("PlagueHive").Tooltip.AddTranslation(GameCulture.Russian,"Сила ваших пчёл и ос будет соперничать с самим Лунным Лордом\nПризывает разрушительную ауру чумы, уничтожающую ближайших врагов, вокруг игрока\nВыпускает пчёл при получении урона\nДружественные пчёлы накладывают дебафф «Чума»\nВсе атаки, включая миньонов накладывают дебафф «Чума»\nСнижает урон от дебаффа «Чума»\nСнаряды создают чумных искателей при попадании во врага");
			calamity.GetItem("ProfanedSoulArtifact").Tooltip.AddTranslation(GameCulture.Russian,"Чистота\nПризывает стража-целителя, который исцеляет определённое количество здоровья каждые несколько секунд\nПризывает стража-защитника, если у вас есть не менее 10 миньон-слотов, который повышает скорость передвижения и сопротивление урону\nПризывает атакующего стража, если на вас надет комплект призывателя таррагона (или сильнее), который увеличивает урон ваших миньонов и число миньон-слотов\nЕсли вы получите удар, большинство их эффектов исчезнет на 5 секунд");
			calamity.GetItem("PsychoticAmulet").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает разбойный и стрелковый урон и шанс критического удара на 5%\nДаёт значительный прирост этим характеристикам, если вы не движетесь");
			calamity.GetItem("RadiantOoze").Tooltip.AddTranslation(GameCulture.Russian,"Вы излучаете свет и быстрее восстанавливаете здоровье ночью");
			calamity.GetItem("RaidersTalisman").Tooltip.AddTranslation(GameCulture.Russian,"Всякий раз, когда вы наносите критический удар разбойным оружием, ваш разбойный урон увеличивается\nЭтот эффект может складываться до 150 раз\nМаксимальное увеличение разбойного урона составляет 15%\nТУТ ModifyTooltips");
			calamity.GetItem("ReaperToothNecklace").Tooltip.AddTranslation(GameCulture.Russian,"Ужасный трофей с высшего хищника\nУвеличивает урон на 12%\nУвеличивает пробивание брони на 100");
			calamity.GetItem("Regenator").Tooltip.AddTranslation(GameCulture.Russian,"Уменьшает максимальный запас здоровья на 50%, но значительно увеличивает его регенерацию");
			calamity.GetItem("RogueEmblem").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает разбойный урон на 15%");
			calamity.GetItem("RoseStone").Tooltip.AddTranslation(GameCulture.Russian,"Одна из древних реликвий\nУвеличивает максимальный запас здоровья на 20, регенерацию здоровья на 1 и шанс критического удара на 3%\nПризывает серного элементаля сражаться за вас");
			calamity.GetItem("RottenBrain").Tooltip.AddTranslation(GameCulture.Russian,"Когда здоровье ниже 75% увеличивает урон на 10%\nКогда здоровье ниже 50% снижает скорость передвижения на 5%\nПри получении урона с неба обрушиваются тени");
			calamity.GetItem("RoverDrive").Tooltip.AddTranslation(GameCulture.Russian,"Активирует защитный экран, который дарует 10 защиты на 10 секунд\nЗатем экран рассеивается и перезаряжается за 20 секунд перед повторным срабатыванием");
			calamity.GetItem("RuinMedallion").Tooltip.AddTranslation(GameCulture.Russian,"Скрытные удары расходуют только 50% вашей максимальной скрытности\nУвеличивает разбойный урон и шанс критического удара на 6%");
			calamity.GetItem("RustyMedallion").Tooltip.AddTranslation(GameCulture.Russian,"Большинство стрелковых оружий иногда выпускают с неба капли кислоты");
			calamity.GetItem("SamuraiBadge").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает урон ближнего боя, истинный урон ближнего боя и скорость атаки ближнего боя, чем ближе вы к врагам\nМаксимальное увеличение урона ближнего боя, истинного урона ближнего боя и скорости атаки ближнего боя составляет 30%");
			calamity.GetItem("SandCloak").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает защиту на 1 и скорость передвижения на 5%\nТУТ ModifyTooltips\nЭтот эффект имеет 30 секунд перезарядки перед повторным использованием");
			calamity.GetItem("ScuttlersJewel").Tooltip.AddTranslation(GameCulture.Russian,"При уничтожении разбойного метательного дротика есть шанс создать драгоценный шип");
			calamity.GetItem("SeaShell").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает защиту и сопротивление урону в жидкостях\nУвеличивает скорость передвижения в жидкостях");
			calamity.GetItem("ShieldoftheOcean").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает защиту на 5 в жидкостях\nУвеличивает скорость передвижения и регенерацию здоровья пока на вас надет комплект прилива");
			calamity.GetItem("SigilofCalamitas").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает магический урон и снижает расход маны на 10%\nУвеличивает дальность подбора звёзд маны и восстанавливает ману при получении урона\nУвеличивает максимальный запас маны на 100\nПоказывает местоположения сокровищ, если видимость аксессуара включена");
			calamity.GetItem("SilencingSheath").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальную скрытность на 20\nСкрытность восстанавливается на 15% быстрее");
			calamity.GetItem("SirensHeart").Tooltip.AddTranslation(GameCulture.Russian,"Превращает носителя в водного элементаля\nПогружение под воду даёт вам бафф\nЗначительно снижает потерю дыхания и обеспечивает небольшим количеством света в бездне\nЗамораживает врагов, когда касаются вас\nВокруг вас слой льда, который поглощает 20% урона, но разбивается после одного удара\nСпустя 30 секунд ледяной щит восстановится\nВау, теперь вы можете плавать\nБольшинство из этих эффектов активны только после победы над Скелетроном\nПредмет режима Месть");
			calamity.GetItem("SpectralVeil").Tooltip.AddTranslation(GameCulture.Russian,"Внутренняя часть плаща полна зубов...\nТУТ ModifyTooltips\nТелепортация отключена пока активен дебафф «Состояние хаоса»\nЕсли вы уворачиваетесь от чего-либо, будучи неуязвимым, вы мгновенно получаете полную скрытность\nПредмет режима Месть");
            calamity.GetItem("SpiritGenerator").Tooltip.AddTranslation(GameCulture.Russian,"Всякий раз, когда ваши миньоны атакуют врага, вы получаете случайный бафф\nЭти баффы на короткое время увеличивают вашу защиту, урон миньонов или регенерацию здоровья");
			calamity.GetItem("Sponge").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость добычи на 50%, а вы излучаете свет\nУвеличивает сопротивление урону на 10% и регенерацию здоровья\nНевосприимчивость к дебаффам «Яд», «Отравление», «Ледяной ожог» и «Охлаждение»\nБафф «Мёд» без штрафа скорости\nУвеличивает максимальный запас здоровья и маны на 20\nБольшинство пчёл/шершней и их снарядов наносят вам только 75% урона\nУвеличивает скорость прыжка на 24% и скорость передвижения на 12%\nСтоя на месте ускоряет регенерацию здоровья и маны\nУвеличивает защиту, скорость передвижения и сопротивление урону в жидкостях\nВраги получают урон, когда бьют вас\nПолучение урона делает вас очень быстрым на короткое время\nВы испустите грибные споры и искры, когда вас ранят\nЧасть урона вражеских атак будет поглощена и использована для исцеления вас");
			calamity.GetItem("StarbusterCore").Tooltip.AddTranslation(GameCulture.Russian,"Атаки миньонов выпускают астральный взрыв при попадании во врага\nУвеличивает максимальное число миньонов на 1");
			calamity.GetItem("StarTaintedGenerator").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальное число миньонов на 2 и урон миньонов на 7%\nАтаки миньонов создают астральные взрывы и накладывают несколько дебаффов");
			calamity.GetItem("StatisBeltOfCurses").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость прыжка на 24% и позволяет постоянно прыгать\nУвеличивает сопротивление урону от падения на 50 блоков\nМожно лазить по стенам, совершать рывок и уворачиваться от атак\nРывки оставляют за собой самонаводящиеся косы");
			calamity.GetItem("StatisBlessing").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальное число миньонов на 2 и урон миньонов на 10%\nУвеличивает отбрасывание миньонов\nАтаки миньонов заставляют врагов плакать при попадании");
			calamity.GetItem("StatisCurse").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает максимальное число миньонов на 3 и урон миньонов на 10%\nУвеличивает отбрасывание миньонов\nДарует миньонам силу теневого пламени\nАтаки миньонов заставляют врагов плакать при попадании");
			calamity.GetItem("StatisNinjaBelt").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость прыжка на 8% и позволяет постоянно прыгать\nУвеличивает сопротивление урону от падения на 35 блоков\nМожно лазить по стенам, совершать рывок и уворачиваться от атак");
			calamity.GetItem("StressPills").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает защиту на 4, максимальную скорость передвижения и ускорение на 5%\nПолучив удар, вы теряете только половину своего максимального адреналина, а не весь\nПредмет режима Месть");
			calamity.GetItem("SupremeBaitTackleBoxFishingStation").Tooltip.AddTranslation(GameCulture.Russian,"Абсолютный рыболовный аксессуар\nУвеличивает навык рыбалки на 80\nЛеска никогда не порвётся и снижает вероятность потребления приманки\nУвеличивает шанс поймать ящики\nЭффект зелья сонара");
			calamity.GetItem("TheAbsorber").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает скорость передвижения на 12%\nУвеличивает скорость прыжка на 24%\nУвеличивает максимальный запас здоровья и маны на 20\nСтоя на месте ускоряет регенерацию здоровья и маны\nУвеличивает защиту, сопротивление урону и скорость передвижения в жидкостях\nВраги получают урон, когда бьют вас\nПолучение урона делает вас очень быстрым на короткое время\nВы испустите грибные споры и искры, когда вас ранят\nУвеличивает сопротивление урону на 5%\nЧасть урона вражеских атак будет поглощена и использована для лечения вас");
			calamity.GetItem("TheAmalgam").Tooltip.AddTranslation(GameCulture.Russian,"Увеличивает урон на 15%\nПри получении урона сбрасывает на врагов тени и серный огонь\nБлижайшие враги получают различные дебаффы, когда бьют вас\nВремя от времени сбрасывает с неба серные огненные шары\nПризывает грибковую глыбу сражаться за вас, которая оставляет после себя ядовитую морскую воду\nУвеличивает скорость передвижения на 75%, весь урон на 10% и защиту на 40 в любых жидкостях\nВышеуказанные бонусы также применяются при прохождении через ядовитую морскую воду глыбы\nВременная невосприимчивость к лаве; значительно уменьшает урон от ожога лавы и увеличивает урон на 15%, находясь в лаве\nУвеличивает скорость передвижение в жидкостях, а также у вас есть разрушающая аура, которая отравляет ближайших врагов");
			calamity.GetItem("TheBee").Tooltip.AddTranslation(GameCulture.Russian,"Заставляет падать звёзды и выпускает пчёл при получении урона\nПри полном здоровье ваш урон увеличен в зависимости от вашего сопротивления урону\nУрон, получаемый при полном здоровье, снижен вдвое\nИмеет 10 секунд перезарядки");
			calamity.GetItem("TheCamper").Tooltip.AddTranslation(GameCulture.Russian,"В покое мы найдём победу\nВы не наносите урона, если вы находитесь в подвижном состоянии\nСтоя на месте дарует бафф(ы) в зависимости от того, какое оружие у вас в руках\nСтоя на месте вы получаете разрушающую ауру вокруг вас\nВо время движения вы восстанавливаете здоровье, как будто стоите на месте\nОбеспечивает небольшим количеством света в бездне");
			calamity.GetItem("TheCommunity").Tooltip.AddTranslation(GameCulture.Russian,"Сердце (большинства) членов сообщества Террарии\nЛегендарный аксессуар\nНачинает со слабого баффа всех ваших характеристик\nХарактеристики становятся более мощными по мере убийства боссов\nУменьшает урон, наносимый вам дебаффами наносящие урон в секунду\nУвеличивает ваше максимальное время полёта на 15%\nСпасибо всем вам, тем, кто поддерживал меня и помог воплотить этот мод в реальность\nПредмет режима Месть");
			calamity.GetItem("TheEvolution").Tooltip.AddTranslation(GameCulture.Russian,"У вас есть 50% шанс отразить снаряды обратно во врага, которые попадут в вас, в виде 1000% от первоначального урона\nЕсли этот эффект срабатывает, вы получаете ускорение регенерации здоровья на короткое время\nЕсли после этого в вас снова попал такой же снаряд, вы заблокируете 15% урона");
			calamity.GetItem("TheFirstShadowflame").Tooltip.AddTranslation(GameCulture.Russian,"Говорят, что в прошлом Прометей спустился с небес, даровать людям огонь\nЕсли это было правдой, то, несомненно, именно демоны ада восстали, чтобы сделать то же самое\nМиньоны накладывают дебафф «Теневое пламя»");
			calamity.GetItem("TheTransformer").Tooltip.AddTranslation(GameCulture.Russian,"Вы испустите заряд искр при получении урона\nИскры наносят дополнительный урон в Хардмоде\nНевосприимчивость к дебаффу «Электрошок» и уменьшает получаемый урон от электрических снарядов и атак врагов\nВражеские пули наносят вам половину урона и отражаются обратно во врага в виде 800% от первоначального урона");
