			calamity.GetItem("EutrophicShank").Tooltip.AddTranslation(GameCulture.Russian,"Стреляет электрическими искрами");
			calamity.GetItem("EvilSmasher").Tooltip.AddTranslation(GameCulture.Russian,"ЗЛО! кРушИТЬ зЛО! КруШИть...АР!");
